import { Storage, File } from "@google-cloud/storage";
import { Response } from "express";
import { randomUUID } from "crypto";
import {
  ObjectAclPolicy,
  ObjectPermission,
  canAccessObject,
  getObjectAclPolicy,
  setObjectAclPolicy,
} from "./objectAcl";

const REPLIT_SIDECAR_ENDPOINT = "http://127.0.0.1:1106";

// The object storage client is used to interact with the object storage service.
export const objectStorageClient = new Storage({
  credentials: {
    audience: "replit",
    subject_token_type: "access_token",
    token_url: `${REPLIT_SIDECAR_ENDPOINT}/token`,
    type: "external_account",
    credential_source: {
      url: `${REPLIT_SIDECAR_ENDPOINT}/credential`,
      format: {
        type: "json",
        subject_token_field_name: "access_token",
      },
    },
    universe_domain: "googleapis.com",
  },
  projectId: "",
});

export class ObjectNotFoundError extends Error {
  constructor() {
    super("Object not found");
    this.name = "ObjectNotFoundError";
    Object.setPrototypeOf(this, ObjectNotFoundError.prototype);
  }
}

// The object storage service is used to interact with the object storage service.
export class ObjectStorageService {
  constructor() {}

  // Gets the public object search paths.
  getPublicObjectSearchPaths(): Array<string> {
    const pathsStr = process.env.PUBLIC_OBJECT_SEARCH_PATHS || "";
    const paths = Array.from(
      new Set(
        pathsStr
          .split(",")
          .map((path) => path.trim())
          .filter((path) => path.length > 0)
      )
    );
    if (paths.length === 0) {
      throw new Error(
        "PUBLIC_OBJECT_SEARCH_PATHS not set. Create a bucket in 'Object Storage' " +
          "tool and set PUBLIC_OBJECT_SEARCH_PATHS env var (comma-separated paths)."
      );
    }
    return paths;
  }

  // Gets the private object directory.
  getPrivateObjectDir(): string {
    const dir = process.env.PRIVATE_OBJECT_DIR || "";
    if (!dir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }
    return dir;
  }

  // Search for a public object from the search paths.
  async searchPublicObject(filePath: string): Promise<File | null> {
    for (const searchPath of this.getPublicObjectSearchPaths()) {
      const fullPath = `${searchPath}/${filePath}`;

      // Full path format: /<bucket_name>/<object_name>
      const { bucketName, objectName } = parseObjectPath(fullPath);
      const bucket = objectStorageClient.bucket(bucketName);
      const file = bucket.file(objectName);

      // Check if file exists
      const [exists] = await file.exists();
      if (exists) {
        return file;
      }
    }

    return null;
  }

  // Downloads an object to the response.
  async downloadObject(file: File, res: Response, cacheTtlSec: number = 3600) {
    try {
      // Get file metadata
      const [metadata] = await file.getMetadata();
      // Get the ACL policy for the object.
      const aclPolicy = await getObjectAclPolicy(file);
      const isPublic = aclPolicy?.visibility === "public";
      // Set appropriate headers
      res.set({
        "Content-Type": metadata.contentType || "application/octet-stream",
        "Content-Length": metadata.size,
        "Cache-Control": `${
          isPublic ? "public" : "private"
        }, max-age=${cacheTtlSec}`,
      });

      // Stream the file to the response
      const stream = file.createReadStream();

      stream.on("error", (err) => {
        console.error("Stream error:", err);
        if (!res.headersSent) {
          res.status(500).json({ error: "Error streaming file" });
        }
      });

      stream.pipe(res);
    } catch (error) {
      console.error("Error downloading file:", error);
      if (!res.headersSent) {
        res.status(500).json({ error: "Error downloading file" });
      }
    }
  }

  // Gets the upload URL for an object entity.
  async getObjectEntityUploadURL(): Promise<string> {
    const privateObjectDir = this.getPrivateObjectDir();
    if (!privateObjectDir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }

    const objectId = randomUUID();
    const fullPath = `${privateObjectDir}/uploads/${objectId}`;

    const { bucketName, objectName } = parseObjectPath(fullPath);

    // Sign URL for PUT method with TTL
    return signObjectURL({
      bucketName,
      objectName,
      method: "PUT",
      ttlSec: 900,
    });
  }

  // Gets the object entity file from the object path.
  async getObjectEntityFile(objectPath: string): Promise<File> {
    if (!objectPath.startsWith("/objects/")) {
      throw new ObjectNotFoundError();
    }

    const parts = objectPath.slice(1).split("/");
    if (parts.length < 2) {
      throw new ObjectNotFoundError();
    }

    const entityId = parts.slice(1).join("/");
    let entityDir = this.getPrivateObjectDir();
    if (!entityDir.endsWith("/")) {
      entityDir = `${entityDir}/`;
    }
    const objectEntityPath = `${entityDir}${entityId}`;
    const { bucketName, objectName } = parseObjectPath(objectEntityPath);
    const bucket = objectStorageClient.bucket(bucketName);
    const objectFile = bucket.file(objectName);
    const [exists] = await objectFile.exists();
    if (!exists) {
      throw new ObjectNotFoundError();
    }
    return objectFile;
  }

  normalizeObjectEntityPath(
    rawPath: string,
  ): string {
    if (!rawPath.startsWith("https://storage.googleapis.com/")) {
      return rawPath;
    }

    // Extract the path from the URL by removing query parameters and domain
    const url = new URL(rawPath);
    const rawObjectPath = url.pathname;

    let objectEntityDir = this.getPrivateObjectDir();
    if (!objectEntityDir.endsWith("/")) {
      objectEntityDir = `${objectEntityDir}/`;
    }

    if (!rawObjectPath.startsWith(objectEntityDir)) {
      return rawObjectPath;
    }

    // Extract the entity ID from the path
    const entityId = rawObjectPath.slice(objectEntityDir.length);
    return `/objects/${entityId}`;
  }

  // Tries to set the ACL policy for the object entity and return the normalized path.
  async trySetObjectEntityAclPolicy(
    rawPath: string,
    aclPolicy: ObjectAclPolicy
  ): Promise<string> {
    const normalizedPath = this.normalizeObjectEntityPath(rawPath);
    if (!normalizedPath.startsWith("/")) {
      return normalizedPath;
    }

    const objectFile = await this.getObjectEntityFile(normalizedPath);
    await setObjectAclPolicy(objectFile, aclPolicy);
    return normalizedPath;
  }

  // Checks if the user can access the object entity.
  async canAccessObjectEntity({
    userId,
    objectFile,
    requestedPermission,
  }: {
    userId?: string;
    objectFile: File;
    requestedPermission?: ObjectPermission;
  }): Promise<boolean> {
    return canAccessObject({
      userId,
      objectFile,
      requestedPermission: requestedPermission ?? ObjectPermission.READ,
    });
  }
}

function parseObjectPath(path: string): {
  bucketName: string;
  objectName: string;
} {
  if (!path.startsWith("/")) {
    path = `/${path}`;
  }
  const pathParts = path.split("/");
  if (pathParts.length < 3) {
    throw new Error("Invalid path: must contain at least a bucket name");
  }

  const bucketName = pathParts[1];
  const objectName = pathParts.slice(2).join("/");

  return {
    bucketName,
    objectName,
  };
}

async function signObjectURL({
  bucketName,
  objectName,
  method,
  ttlSec,
}: {
  bucketName: string;
  objectName: string;
  method: "GET" | "PUT" | "DELETE" | "HEAD";
  ttlSec: number;
}): Promise<string> {
  const request = {
    bucket_name: bucketName,
    object_name: objectName,
    method,
    expires_at: new Date(Date.now() + ttlSec * 1000).toISOString(),
  };
  const response = await fetch(
    `${REPLIT_SIDECAR_ENDPOINT}/object-storage/signed-object-url`,
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(request),
    }
  );
  if (!response.ok) {
    throw new Error(
      `Failed to sign object URL, errorcode: ${response.status}, ` +
        `make sure you're running on Replit`
    );
  }

  const { signed_url: signedURL } = await response.json();
  return signedURL;
}

server/recruitmentService.ts.content:
import { storage } from "./storage";

export interface CandidateScore {
  applicationId: number;
  candidate: any;
  job: any;
  autoScore: number;
  manualScore?: number;
  totalScore: number;
  factors: {
    experienceMatch: number;
    skillsMatch: number;
    availabilityScore: number;
    salaryFit: number;
    applicationQuality: number;
  };
}

export class RecruitmentService {
  /**
   * Calcule le score automatique d'un candidat basé sur plusieurs critères
   */
  calculateAutoScore(application: any, job: any): { score: number, factors: any } {
    let totalScore = 0;
    const factors = {
      experienceMatch: 0,
      skillsMatch: 0,
      availabilityScore: 0,
      salaryFit: 0,
      applicationQuality: 0
    };

    // 1. Correspondance d'expérience (25 points max)
    if (job.experienceLevel && application.experienceLevel) {
      const experienceMapping = { 'Débutant': 1, 'Intermédiaire': 2, 'Senior': 3 };
      const jobExp = experienceMapping[job.experienceLevel] || 2;
      const candidateExp = experienceMapping[application.experienceLevel] || 2;

      if (candidateExp >= jobExp) {
        factors.experienceMatch = 25;
      } else if (candidateExp === jobExp - 1) {
        factors.experienceMatch = 15;
      } else {
        factors.experienceMatch = 5;
      }
    } else {
      factors.experienceMatch = 10; // Score neutre si pas d'info
    }

    // 2. Correspondance des compétences (30 points max)
    if (job.skills && job.skills.length > 0 && application.skills) {
      const jobSkills = job.skills.map((s: string) => s.toLowerCase());
      const candidateSkills = application.skills.map((s: string) => s.toLowerCase());

      const matchingSkills = jobSkills.filter((skill: string) => 
        candidateSkills.some((cs: string) => cs.includes(skill) || skill.includes(cs))
      );

      const skillsMatchRatio = matchingSkills.length / jobSkills.length;
      factors.skillsMatch = Math.round(skillsMatchRatio * 30);
    } else {
      factors.skillsMatch = 15; // Score neutre
    }

    // 3. Disponibilité (15 points max)
    if (application.availabilityDate) {
      const availDate = new Date(application.availabilityDate);
      const now = new Date();
      const daysDiff = Math.ceil((availDate.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));

      if (daysDiff <= 0) {
        factors.availabilityScore = 15; // Disponible immédiatement
      } else if (daysDiff <= 30) {
        factors.availabilityScore = 12; // Dans le mois
      } else if (daysDiff <= 60) {
        factors.availabilityScore = 8; // Dans les 2 mois
      } else {
        factors.availabilityScore = 3; // Plus tard
      }
    } else {
      factors.availabilityScore = 12; // Assume disponible bientôt
    }

    // 4. Adéquation salariale (15 points max)
    if (application.salaryExpectation && job.salary) {
      // Parse des fourchettes de salaire (format: "40k - 55k €")
      const parseSalary = (salaryStr: string): number => {
        const match = salaryStr.match(/(\d+)k/);
        return match ? parseInt(match[1]) * 1000 : 0;
      };

      const candidateSalary = parseSalary(application.salaryExpectation);
      const jobSalaryMatch = job.salary.match(/(\d+)k\s*-\s*(\d+)k/);

      if (jobSalaryMatch) {
        const jobMinSalary = parseInt(jobSalaryMatch[1]) * 1000;
        const jobMaxSalary = parseInt(jobSalaryMatch[2]) * 1000;

        if (candidateSalary >= jobMinSalary && candidateSalary <= jobMaxSalary) {
          factors.salaryFit = 15; // Parfait match
        } else if (candidateSalary <= jobMaxSalary * 1.1) {
          factors.salaryFit = 10; // Proche
        } else {
          factors.salaryFit = 3; // Trop élevé
        }
      } else {
        factors.salaryFit = 8; // Score neutre
      }
    } else {
      factors.salaryFit = 10; // Score neutre si pas d'info
    }

    // 5. Qualité de la candidature (15 points max)
    let qualityScore = 0;
    if (application.coverLetter && application.coverLetter.length > 100) {
      qualityScore += 5; // Lettre de motivation détaillée
    }
    if (application.cvPath) {
      qualityScore += 5; // CV fourni
    }
    if (application.motivationLetterPath) {
      qualityScore += 3; // Lettre de motivation en fichier
    }
    if (application.phone) {
      qualityScore += 2; // Contact fourni
    }
    factors.applicationQuality = Math.min(qualityScore, 15);

    // Score total (sur 100)
    totalScore = factors.experienceMatch + factors.skillsMatch + 
                factors.availabilityScore + factors.salaryFit + factors.applicationQuality;

    return { score: Math.min(totalScore, 100), factors };
  }

  /**
   * Obtient le Top 10 des candidats pour une offre donnée
   */
  async getTopCandidates(jobId: number, limit: number = 10): Promise<CandidateScore[]> {
    const applications = await storage.getApplicationsForJob(jobId);
    const job = await storage.getJob(jobId);

    if (!job) {
      throw new Error("Job not found");
    }

    const scoredCandidates: CandidateScore[] = [];

    for (const application of applications) {
      const candidate = await storage.getUser(application.userId);
      const { score, factors } = this.calculateAutoScore(application, job);

      // Mise à jour du score automatique dans la BD
      await storage.updateApplication(application.id, { autoScore: score });

      const totalScore = application.manualScore 
        ? Math.round((score * 0.6) + (application.manualScore * 0.4)) // 60% auto, 40% manuel
        : score;

      scoredCandidates.push({
        applicationId: application.id,
        candidate,
        job,
        autoScore: score,
        manualScore: application.manualScore,
        totalScore,
        factors
      });
    }

    // Tri par score décroissant
    return scoredCandidates
      .sort((a, b) => b.totalScore - a.totalScore)
      .slice(0, limit);
  }

  /**
   * Affecte des candidats à un recruteur
   */
  async assignCandidatesToRecruiter(applicationIds: number[], recruiterId: string): Promise<void> {
    for (const appId of applicationIds) {
      await storage.updateApplication(appId, { 
        assignedRecruiter: recruiterId,
        status: 'assigned'
      });
    }
  }

  /**
   * Met à jour la note manuelle d'un candidat
   */
  async updateManualScore(applicationId: number, score: number, notes?: string): Promise<void> {
    await storage.updateApplication(applicationId, { 
      manualScore: score,
      scoreNotes: notes,
      status: 'scored'
    });
  }

  /**
   * Obtient les candidatures assignées à un recruteur
   */
  async getAssignedApplications(recruiterId: string): Promise<any[]> {
    const applications = await storage.getApplicationsByRecruiter(recruiterId);

    const enrichedApps = [];
    for (const app of applications) {
      const candidate = await storage.getUser(app.userId);
      const job = await storage.getJob(app.jobId);
      enrichedApps.push({ ...app, candidate, job });
    }

    return enrichedApps;
  }

  /**
   * Obtient le Top 3 final après notation manuelle
   */
  async getFinalTop3(jobId: number): Promise<CandidateScore[]> {
    const topCandidates = await this.getTopCandidates(jobId, 100);

    // Filtre seulement ceux qui ont une note manuelle
    const scoredCandidates = topCandidates.filter(c => c.manualScore !== undefined);

    return scoredCandidates
      .sort((a, b) => b.totalScore - a.totalScore)
      .slice(0, 3);
  }
}

export const recruitmentService = new RecruitmentService();

server/replitAuth.ts.content:
import * as client from "openid-client";
import { Strategy, type VerifyFunction } from "openid-client/passport";

import passport from "passport";
import session from "express-session";
import type { Express, RequestHandler } from "express";
import memoize from "memoizee";
import connectPg from "connect-pg-simple";
import { storage } from "./storage";

if (!process.env.REPLIT_DOMAINS) {
  throw new Error("Environment variable REPLIT_DOMAINS not provided");
}

const getOidcConfig = memoize(
  async () => {
    return await client.discovery(
      new URL(process.env.ISSUER_URL ?? "https://replit.com/oidc"),
      process.env.REPL_ID!
    );
  },
  { maxAge: 3600 * 1000 }
);

export function getSession() {
  const sessionTtl = 7 * 24 * 60 * 60 * 1000; // 1 week
  const pgStore = connectPg(session);
  const sessionStore = new pgStore({
    conString: process.env.DATABASE_URL,
    createTableIfMissing: false,
    ttl: sessionTtl,
    tableName: "sessions",
  });
  return session({
    secret: process.env.SESSION_SECRET || "dev-secret-key-not-secure",
    store: sessionStore,
    resave: false,
    saveUninitialized: false,
    cookie: {
      httpOnly: true,
      secure: process.env.NODE_ENV === "production", // Only secure in production
      maxAge: sessionTtl,
    },
  });
}

function updateUserSession(
  user: any,
  tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers
) {
  user.claims = tokens.claims();
  user.access_token = tokens.access_token;
  user.refresh_token = tokens.refresh_token;
  user.expires_at = user.claims?.exp;
}

async function upsertUser(
  claims: any,
) {
  await storage.upsertUser({
    id: claims["sub"],
    email: claims["email"],
    firstName: claims["first_name"],
    lastName: claims["last_name"],
    profileImageUrl: claims["profile_image_url"],
  });
}

export async function setupAuth(app: Express) {
  app.set("trust proxy", 1);
  app.use(getSession());
  app.use(passport.initialize());
  app.use(passport.session());

  const config = await getOidcConfig();

  const verify: VerifyFunction = async (
    tokens: client.TokenEndpointResponse & client.TokenEndpointResponseHelpers,
    verified: passport.AuthenticateCallback
  ) => {
    const user = {};
    updateUserSession(user, tokens);
    await upsertUser(tokens.claims());
    verified(null, user);
  };

  for (const domain of process.env
    .REPLIT_DOMAINS!.split(",")) {
    const strategy = new Strategy(
      {
        name: `replitauth:${domain}`,
        config,
        scope: "openid email profile offline_access",
        callbackURL: `https://${domain}/api/callback`,
      },
      verify,
    );
    passport.use(strategy);
  }

  passport.serializeUser((user: Express.User, cb) => cb(null, user));
  passport.deserializeUser((user: Express.User, cb) => cb(null, user));

  app.get("/api/login", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      prompt: "login consent",
      scope: ["openid", "email", "profile", "offline_access"],
    })(req, res, next);
  });

  app.get("/api/callback", (req, res, next) => {
    passport.authenticate(`replitauth:${req.hostname}`, {
      successReturnToOrRedirect: "/",
      failureRedirect: "/api/login",
    })(req, res, next);
  });

  app.get("/api/logout", (req, res) => {
    req.logout(() => {
      // Détruire la session
      req.session.destroy((err) => {
        if (err) {
          console.error('Error destroying session:', err);
        }
        // Clear the session cookie
        res.clearCookie('connect.sid');

        // Return HTML with immediate redirect
        res.send(`
          <!DOCTYPE html>
          <html>
          <head>
            <meta charset="UTF-8">
            <title>Déconnexion...</title>
            <script>
              window.location.href = '/';
            </script>
          </head>
          <body>
            <p>Déconnexion en cours...</p>
            <script>
              // Fallback si le premier script ne fonctionne pas
              setTimeout(() => window.location.href = '/', 100);
            </script>
          </body>
          </html>
        `);
      });
    });
  });
}

export const isAuthenticated: RequestHandler = async (req, res, next) => {
  const user = req.user as any;

  if (!req.isAuthenticated() || !user.expires_at) {
    return res.status(401).json({ message: "Unauthorized" });
  }

  const now = Math.floor(Date.now() / 1000);
  if (now <= user.expires_at) {
    return next();
  }

  const refreshToken = user.refresh_token;
  if (!refreshToken) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }

  try {
    const config = await getOidcConfig();
    const tokenResponse = await client.refreshTokenGrant(config, refreshToken);
    updateUserSession(user, tokenResponse);
    return next();
  } catch (error) {
    res.status(401).json({ message: "Unauthorized" });
    return;
  }
};

server/routes.ts.content:
import { Express } from "express";
import type { Server } from "http";
import { storage } from "./storage";
import { registerAuthRoutes } from "./authRoutes";
import { insertJobSchema, insertApplicationSchema, updateApplicationSchema } from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  const { createServer } = await import("http");

  // Configuration des sessions (nécessaire pour l'authentification)
  const { getSession } = await import("./replitAuth");
  app.use(getSession());

  // Enregistrer les routes d'authentification email/password
  registerAuthRoutes(app);

  // Middleware d'authentification simplifié
  const requireAuth = (req: any, res: any, next: any) => {
    const sessionUser = (req.session as any)?.user;
    if (!sessionUser) {
      return res.status(401).json({ message: "Non connecté" });
    }
    req.user = sessionUser;
    next();
  };

  const requireAdminRole = async (req: any, res: any, next: any) => {
    const user = req.user;
    if (!user?.role || !["admin", "hr", "recruiter"].includes(user.role)) {
      return res.status(403).json({ message: "Access denied" });
    }
    next();
  };

  // Routes publiques - Jobs
  app.get("/api/jobs", async (req, res) => {
    try {
      const jobs = await storage.getAllJobs();
      res.json(jobs);
    } catch (error) {
      console.error("Error fetching jobs:", error);
      res.status(500).json({ message: "Failed to fetch jobs" });
    }
  });

  app.get("/api/jobs/:id", async (req, res) => {
    try {
      const jobId = parseInt(req.params.id);
      const job = await storage.getJob(jobId);
      if (!job) {
        return res.status(404).json({ message: "Job not found" });
      }
      res.json(job);
    } catch (error) {
      console.error("Error fetching job:", error);
      res.status(500).json({ message: "Failed to fetch job" });
    }
  });

  // Routes authentifiées - Applications
  app.get("/api/applications", requireAuth, async (req, res) => {
    try {
      const userId = (req.user as any)?.id;
      if (!userId) {
        return res.status(401).json({ message: "User not authenticated" });
      }
      const applications = await storage.getApplicationsByUser(userId);
      res.json(applications);
    } catch (error) {
      console.error("Error fetching applications:", error);
      res.status(500).json({ message: "Failed to fetch applications" });
    }
  });

  app.post("/api/applications", requireAuth, async (req, res) => {
    try {
      const userId = (req.user as any)?.id;
      if (!userId) {
        return res.status(401).json({ message: "User not authenticated" });
      }

      // Validation des données avec le schéma Zod
      const validatedData = insertApplicationSchema.parse(req.body);

      // Convert availability string to Date if provided
      if (validatedData.availability) {
        validatedData.availability = new Date(validatedData.availability);
      }

      // Création de la candidature via le storage
      const application = await storage.createApplication(validatedData, userId);
      res.status(201).json(application);
    } catch (error: any) {
      console.error("Error creating application:", error);

      if (error?.name === 'ZodError') {
        return res.status(400).json({ 
          message: "Données invalides", 
          errors: error.errors 
        });
      }

      res.status(500).json({ message: "Failed to create application" });
    }
  });

  // Routes admin
  app.get("/api/admin/jobs", requireAuth, requireAdminRole, async (req, res) => {
    try {
      const jobs = await storage.getAllJobs();
      res.json(jobs);
    } catch (error) {
      console.error("Error fetching admin jobs:", error);
      res.status(500).json({ message: "Failed to fetch jobs" });
    }
  });

  app.post("/api/admin/jobs", requireAuth, requireAdminRole, async (req, res) => {
    try {
      // Validation des données avec le schéma Zod
      const validatedData = insertJobSchema.parse(req.body);

      // Création de l'emploi via le storage
      const newJob = await storage.createJob(validatedData);

      res.status(201).json(newJob);
    } catch (error: any) {
      console.error("Error creating job:", error);

      if (error?.name === 'ZodError') {
        return res.status(400).json({ 
          message: "Données invalides", 
          errors: error.errors 
        });
      }

      res.status(500).json({ message: "Failed to create job" });
    }
  });

  app.patch("/api/admin/jobs/:id", requireAuth, requireAdminRole, async (req, res) => {
    try {
      const jobId = parseInt(req.params.id);
      if (isNaN(jobId)) {
        return res.status(400).json({ message: "ID d'emploi invalide" });
      }

      // Validation partielle des données avec le schéma Zod
      const validatedData = insertJobSchema.partial().parse(req.body);

      // Mise à jour de l'emploi via le storage
      const updatedJob = await storage.updateJob(jobId, validatedData);

      if (!updatedJob) {
        return res.status(404).json({ message: "Emploi non trouvé" });
      }

      res.json(updatedJob);
    } catch (error: any) {
      console.error("Error updating job:", error);

      if (error?.name === 'ZodError') {
        return res.status(400).json({ 
          message: "Données invalides", 
          errors: error.errors 
        });
      }

      res.status(500).json({ message: "Failed to update job" });
    }
  });

  app.delete("/api/admin/jobs/:id", requireAuth, requireAdminRole, async (req, res) => {
    try {
      const jobId = parseInt(req.params.id);
      if (isNaN(jobId)) {
        return res.status(400).json({ message: "ID d'emploi invalide" });
      }

      const deleted = await storage.deleteJob(jobId);

      if (!deleted) {
        return res.status(404).json({ message: "Emploi non trouvé" });
      }

      res.json({ message: "Emploi supprimé avec succès" });
    } catch (error: any) {
      console.error("Error deleting job:", error);
      res.status(500).json({ message: "Failed to delete job" });
    }
  });

  app.get("/api/admin/applications", requireAuth, requireAdminRole, async (req, res) => {
    try {
      const applications = await storage.getAllApplications();
      res.json(applications);
    } catch (error) {
      console.error("Error fetching admin applications:", error);
      res.status(500).json({ message: "Failed to fetch applications" });
    }
  });

  app.patch("/api/admin/applications/:id", requireAuth, requireAdminRole, async (req, res) => {
    try {
      const applicationId = parseInt(req.params.id);
      if (isNaN(applicationId)) {
        return res.status(400).json({ message: "ID de candidature invalide" });
      }

      // Validation partielle des données avec le schéma Zod
      const validatedData = updateApplicationSchema.parse(req.body);

      // Mise à jour de la candidature via le storage
      const updatedApplication = await storage.updateApplication(applicationId, validatedData);

      if (!updatedApplication) {
        return res.status(404).json({ message: "Candidature non trouvée" });
      }

      res.json(updatedApplication);
    } catch (error: any) {
      console.error("Error updating application:", error);

      if (error?.name === 'ZodError') {
        return res.status(400).json({ 
          message: "Données invalides", 
          errors: error.errors 
        });
      }

      res.status(500).json({ message: "Failed to update application" });
    }
  });

  app.delete("/api/admin/applications/:id", requireAuth, requireAdminRole, async (req, res) => {
    try {
      const applicationId = parseInt(req.params.id);
      if (isNaN(applicationId)) {
        return res.status(400).json({ message: "ID de candidature invalide" });
      }

      const deleted = await storage.deleteApplication(applicationId);

      if (!deleted) {
        return res.status(404).json({ message: "Candidature non trouvée" });
      }

      res.json({ message: "Candidature supprimée avec succès" });
    } catch (error: any) {
      console.error("Error deleting application:", error);
      res.status(500).json({ message: "Failed to delete application" });
    }
  });

  app.get("/api/admin/recruiters", requireAuth, requireAdminRole, async (req, res) => {
    try {
      const recruiters = await storage.getRecruiters();
      res.json(recruiters);
    } catch (error) {
      console.error("Error fetching recruiters:", error);
      res.status(500).json({ message: "Failed to fetch recruiters" });
    }
  });

  // Routes utilisateur
  app.get("/api/users/:id", requireAuth, async (req, res) => {
    try {
      const userId = req.params.id;
      const user = await storage.getUser(userId);
      if (!user) {
        return res.status(404).json({ message: "User not found" });
      }
      res.json(user);
    } catch (error) {
      console.error("Error fetching user:", error);
      res.status(500).json({ message: "Failed to fetch user" });
    }
  });

  app.put("/api/users/:id", requireAuth, async (req, res) => {
    try {
      const userId = req.params.id;

      // Vérification des permissions
      if (userId !== (req.user as any)?.id && !["admin", "hr"].includes((req.user as any)?.role || "")) {
        return res.status(403).json({ message: "Access denied" });
      }

      const updatedUser = await storage.updateUser(userId, req.body);
      res.json(updatedUser);
    } catch (error) {
      console.error("Error updating user:", error);
      res.status(500).json({ message: "Failed to update user" });
    }
  });

  // Routes de gestion de la paie
  app.get("/api/payroll", requireAuth, async (req, res) => {
    try {
      const user = req.user as any;

      if (!["admin", "hr"].includes(user.role)) {
        return res.status(403).json({ message: "Accès refusé" });
      }

      const payrolls = await storage.getAllPayrolls();
      res.json(payrolls);
    } catch (error) {
      console.error("Error fetching payrolls:", error);
      res.status(500).json({ message: "Failed to fetch payrolls" });
    }
  });

  app.post("/api/payroll", requireAuth, async (req, res) => {
    try {
      const user = req.user as any;

      if (!["admin", "hr"].includes(user.role)) {
        return res.status(403).json({ message: "Accès refusé" });
      }

      const payrollData = {
        ...req.body,
        createdBy: user.id
      };

      const newPayroll = await storage.createPayroll(payrollData);
      res.status(201).json(newPayroll);
    } catch (error) {
      console.error("Error creating payroll:", error);
      res.status(500).json({ message: "Failed to create payroll" });
    }
  });

  app.put("/api/payroll/:id", requireAuth, async (req, res) => {
    try {
      const user = req.user as any;

      if (!["admin", "hr"].includes(user.role)) {
        return res.status(403).json({ message: "Accès refusé" });
      }

      const payrollId = parseInt(req.params.id);
      const updatedPayroll = await storage.updatePayroll(payrollId, req.body);
      res.json(updatedPayroll);
    } catch (error) {
      console.error("Error updating payroll:", error);
      res.status(500).json({ message: "Failed to update payroll" });
    }
  });

  app.get("/api/payroll/:id/payslip", requireAuth, async (req, res) => {
    try {
      const user = req.user as any;

      if (!["admin", "hr"].includes(user.role)) {
        return res.status(403).json({ message: "Accès refusé" });
      }

      const payrollId = parseInt(req.params.id);
      const payroll = await storage.getPayroll(payrollId);

      if (!payroll) {
        return res.status(404).json({ message: "Bulletin de paie non trouvé" });
      }

      res.json(payroll);
    } catch (error) {
      console.error("Error fetching payslip:", error);
      res.status(500).json({ message: "Failed to fetch payslip" });
    }
  });

  app.post("/api/payroll/:id/generate-pdf", requireAuth, async (req, res) => {
    try {
      const user = req.user as any;

      if (!["admin", "hr"].includes(user.role)) {
        return res.status(403).json({ message: "Accès refusé" });
      }

      const payrollId = parseInt(req.params.id);
      // TODO: Implémenter génération PDF du bulletin de paie
      res.json({ message: "PDF generation not implemented yet" });
    } catch (error) {
      console.error("Error generating PDF:", error);
      res.status(500).json({ message: "Failed to generate PDF" });
    }
  });

  app.post("/api/payroll/:id/send-email", requireAuth, async (req, res) => {
    try {
      const user = req.user as any;

      if (!["admin", "hr"].includes(user.role)) {
        return res.status(403).json({ message: "Accès refusé" });
      }

      const payrollId = parseInt(req.params.id);
      const { email, customMessage } = req.body;

      // TODO: Implémenter envoi email du bulletin de paie
      res.json({ message: "Email sending not implemented yet" });
    } catch (error) {
      console.error("Error sending email:", error);
      res.status(500).json({ message: "Failed to send email" });
    }
  });

  // Routes des employés pour la paie
  app.get("/api/employees", requireAuth, async (req, res) => {
    try {
      const user = req.user as any;

      if (!["admin", "hr"].includes(user.role)) {
        return res.status(403).json({ message: "Accès refusé" });
      }

      const employees = await storage.getAllEmployees();
      res.json(employees);
    } catch (error) {
      console.error("Error fetching employees:", error);
      res.status(500).json({ message: "Failed to fetch employees" });
    }
  });

  // Complete user profile
  app.put("/api/profile/complete", requireAuth, async (req: any, res) => {
    try {
      const userId = req.user.id;
      const profileData = {
        ...req.body,
        profileCompleted: true
      };

      const updatedUser = await storage.updateUser(userId, profileData);
      res.json(updatedUser);
    } catch (error) {
      console.error("Error completing profile:", error);
      res.status(500).json({ message: "Failed to complete profile" });
    }
  });

  // Cette ligne sera remplacée par le middleware Vite en développement

  // Créer et retourner le serveur HTTP
  const server = createServer(app);
  return server;
}

