      const response = await fetch('/api/admin/jobs', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          ...jobData,
          isActive: true
        })
      });
      if (!response.ok) {
        const error = await response.text();
        throw new Error(error || 'Erreur lors de la création de l\'offre');
      }
      return response.json();
