import { useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Link } from "wouter";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  Briefcase, 
  Bell, 
  User, 
  LogOut, 
  LayoutDashboard,
  FileText,
  Search,
  Folder,
  Building2,
  MapPin,
  Calendar,
  Eye
} from "lucide-react";
import { LanguageSelector } from "@/components/LanguageSelector";
import { formatDistanceToNow } from "date-fns";
import { fr, enUS, pt } from "date-fns/locale";
import { getLanguage } from "@/lib/i18n";
import { ApplicationTimeline } from "@/components/ApplicationTimeline";

const locales = {
  fr: fr,
  en: enUS,
  pt: pt,
};

export default function Applications() {
  const { user, isLoading, isAuthenticated } = useAuth();
  const { toast } = useToast();
  const currentLang = getLanguage();
  const locale = locales[currentLang];

  const { data: applications = [], isLoading: applicationsLoading } = useQuery({
    queryKey: ["/api/applications"],
    enabled: isAuthenticated,
  });

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Non autorisé",
        description: "Vous devez être connecté. Redirection vers la connexion...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
          <p className="mt-4 text-muted-foreground">Chargement...</p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated || !user) {
    return null;
  }

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending':
        return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-100';
      case 'reviewed':
        return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-100';
      case 'interview':
        return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-100';
      case 'accepted':
        return 'bg-emerald-100 text-emerald-800 dark:bg-emerald-900 dark:text-emerald-100';
      case 'rejected':
        return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-100';
      default:
        return 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-100';
    }
  };

  const getStatusText = (status: string) => {
    const translations = {
      pending: "En attente",
      reviewed: "Examinée", 
      interview: "Entretien planifié",
      accepted: "Acceptée",
      rejected: "Refusée",
    };
    return translations[status as keyof typeof translations] || status;
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="bg-card shadow-sm border-b border-border">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-8">
              <div className="flex items-center space-x-2">
                <Briefcase className="h-8 w-8 text-primary" />
                <span className="text-xl font-bold text-foreground">JobPortal</span>
                <span className="text-sm text-muted-foreground">Espace candidat</span>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <LanguageSelector />

              <Button variant="ghost" size="icon" className="relative" data-testid="button-notifications">
                <Bell className="h-5 w-5" />
                <span className="absolute -top-1 -right-1 h-4 w-4 bg-destructive text-destructive-foreground text-xs rounded-full flex items-center justify-center">
                  3
                </span>
              </Button>

              <div className="flex items-center space-x-2">
                <div className="h-8 w-8 bg-primary text-primary-foreground rounded-full flex items-center justify-center text-sm font-medium">
                  {user.firstName?.[0] || user.email?.[0] || 'U'}
                </div>
                <span className="text-sm font-medium" data-testid="text-user-name">
                  {user.firstName && user.lastName ? `${user.firstName} ${user.lastName}` : user.email}
                </span>
                <Button 
                  variant="ghost" 
                  size="icon"
                  onClick={() => import("@/lib/logout").then(m => m.handleLogout())}
                  data-testid="button-logout"
                  title="Se déconnecter"
                >
                  <LogOut className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </div>
        </div>
      </header>

      <div className="flex">
        {/* Sidebar Navigation */}
        <nav className="w-64 bg-card shadow-sm border-r border-border min-h-screen">
          <div className="p-6">
            <ul className="space-y-2">
              <li>
                <Link href="/">
                  <a className="flex items-center space-x-3 p-3 text-muted-foreground hover:text-foreground hover:bg-accent rounded-md transition-colors" data-testid="link-dashboard">
                    <LayoutDashboard className="h-5 w-5" />
                    <span>Tableau de bord</span>
                  </a>
                </Link>
              </li>
              <li>
                <a href="#applications" className="flex items-center space-x-3 p-3 text-primary bg-primary/10 rounded-md">
                  <FileText className="h-5 w-5" />
                  <span className="font-medium">Mes candidatures</span>
                </a>
              </li>
              <li>
                <Link href="/profile">
                  <a className="flex items-center space-x-3 p-3 text-muted-foreground hover:text-foreground hover:bg-accent rounded-md transition-colors" data-testid="link-profile">
                    <User className="h-5 w-5" />
                    <span>Mon profil</span>
                  </a>
                </Link>
              </li>
              <li>
                <Link href="/jobs">
                  <a className="flex items-center space-x-3 p-3 text-muted-foreground hover:text-foreground hover:bg-accent rounded-md transition-colors" data-testid="link-search">
                    <Search className="h-5 w-5" />
                    <span>Rechercher</span>
                  </a>
                </Link>
              </li>
              <li>
                <a href="#documents" className="flex items-center space-x-3 p-3 text-muted-foreground hover:text-foreground hover:bg-accent rounded-md transition-colors" data-testid="link-documents">
                  <Folder className="h-5 w-5" />
                  <span>Mes documents</span>
                </a>
              </li>
            </ul>
          </div>
        </nav>

        {/* Main Content */}
        <main className="flex-1 p-8 bg-background">
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-foreground mb-2" data-testid="text-applications-title">
              Mes candidatures
            </h1>
            <p className="text-muted-foreground">
              Suivez l'état de vos candidatures et gérez votre parcours professionnel
            </p>
          </div>

          {applicationsLoading ? (
            <div className="space-y-4">
              {[1, 2, 3, 4].map((i) => (
                <Card key={i} className="animate-pulse">
                  <CardContent className="p-6">
                    <div className="h-4 bg-muted rounded w-3/4 mb-2"></div>
                    <div className="h-3 bg-muted rounded w-1/2 mb-4"></div>
                    <div className="h-3 bg-muted rounded w-full mb-2"></div>
                    <div className="h-3 bg-muted rounded w-2/3"></div>
                  </CardContent>
                </Card>
              ))}
            </div>
          ) : applications.length === 0 ? (
            <Card>
              <CardContent className="p-8 text-center">
                <FileText className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                <h3 className="text-lg font-semibold text-foreground mb-2">Aucune candidature</h3>
                <p className="text-muted-foreground mb-4" data-testid="text-no-applications">
                  Vous n'avez pas encore envoyé de candidature. Découvrez nos offres d'emploi !
                </p>
                <Link href="/jobs">
                  <Button data-testid="button-browse-jobs">
                    Parcourir les offres
                  </Button>
                </Link>
              </CardContent>
            </Card>
          ) : (
            <div className="space-y-4">
              {applications.map((application) => (
                <Card 
                  key={application.id}
                  className="hover:shadow-md transition-shadow"
                  data-testid={`card-application-${application.id}`}
                >
                  <CardContent className="p-6">
                    <div className="flex justify-between items-start mb-4">
                      <div className="flex-1">
                        <h3 className="text-lg font-semibold text-foreground mb-2" data-testid={`text-job-title-${application.id}`}>
                          {application.job?.title || 'Poste supprimé'}
                        </h3>
                        <div className="flex items-center space-x-4 text-sm text-muted-foreground">
                          <span className="flex items-center">
                            <Building2 className="h-4 w-4 mr-1" />
                            <span data-testid={`text-company-${application.id}`}>{application.job?.company || 'Entreprise inconnue'}</span>
                          </span>
                          <span className="flex items-center">
                            <MapPin className="h-4 w-4 mr-1" />
                            <span data-testid={`text-location-${application.id}`}>{application.job?.location || 'Location inconnue'}</span>
                          </span>
                          <span className="flex items-center">
                            <Calendar className="h-4 w-4 mr-1" />
                            <span data-testid={`text-applied-date-${application.id}`}>
                              Candidature envoyée {formatDistanceToNow(application.createdAt, { addSuffix: true, locale })}
                            </span>
                          </span>
                        </div>
                      </div>
                      <div className="text-right">
                        <Badge 
                          className={getStatusColor(application.status)}
                          data-testid={`badge-status-${application.id}`}
                        >
                          {getStatusText(application.status)}
                        </Badge>
                      </div>
                    </div>

                    {application.coverLetter && (
                      <div className="mb-4">
                        <p className="text-sm text-muted-foreground line-clamp-2" data-testid={`text-cover-letter-${application.id}`}>
                          {application.coverLetter}
                        </p>
                      </div>
                    )}

                    <div className="flex justify-between items-center mb-6">
                      <div className="flex items-center space-x-4 text-sm text-muted-foreground">
                        {application.salaryExpectation && (
                          <span data-testid={`text-salary-expectation-${application.id}`}>
                            Prétentions: {application.salaryExpectation}
                          </span>
                        )}
                        {application.availabilityDate && (
                          <span data-testid={`text-availability-${application.id}`}>
                            Disponible: {new Date(application.availabilityDate).toLocaleDateString()}
                          </span>
                        )}
                      </div>
                      <Button 
                        variant="outline" 
                        size="sm"
                        className="flex items-center space-x-2"
                        data-testid={`button-view-application-${application.id}`}
                      >
                        <Eye className="h-4 w-4" />
                        <span>Voir détails</span>
                      </Button>
                    </div>

                    {/* Timeline de suivi pour chaque candidature */}
                    <div className="border-t pt-6">
                      <ApplicationTimeline application={application} />
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </main>
      </div>
    </div>
  );
}

client/src/pages/CandidateAssignment.tsx.content:
import { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Checkbox } from "@/components/ui/checkbox";
import { 
  Users, 
  Award, 
  TrendingUp, 
  UserCheck,
  Briefcase,
  LogOut,
  Star,
  Target
} from "lucide-react";
import { useAuth } from "@/hooks/useAuth";
import { Link } from "wouter";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface CandidateScore {
  applicationId: number;
  candidate: any;
  job: any;
  autoScore: number;
  totalScore: number;
  factors: {
    experienceMatch: number;
    skillsMatch: number;
    availabilityScore: number;
    salaryFit: number;
    applicationQuality: number;
  };
}

export default function CandidateAssignment() {
  const { user } = useAuth();
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedJob, setSelectedJob] = useState<string>("");
  const [selectedCandidates, setSelectedCandidates] = useState<number[]>([]);
  const [selectedRecruiter, setSelectedRecruiter] = useState<string>("");

  const { data: jobs = [] } = useQuery({
    queryKey: ["/api/admin/jobs"],
  });

  const { data: recruiters = [] } = useQuery({
    queryKey: ["/api/admin/recruiters"],
  });

  const { data: topCandidates = [], isLoading } = useQuery({
    queryKey: ["/api/admin/top-candidates", selectedJob],
    enabled: !!selectedJob,
  });

  const assignMutation = useMutation({
    mutationFn: async ({ candidateIds, recruiterId }: { candidateIds: number[], recruiterId: string }) => {
      await apiRequest("POST", "/api/admin/assign-candidates", {
        applicationIds: candidateIds,
        recruiterId
      });
    },
    onSuccess: () => {
      toast({
        title: "Affectation réussie",
        description: "Les candidats ont été assignés avec succès.",
      });
      setSelectedCandidates([]);
      setSelectedRecruiter("");
      queryClient.invalidateQueries({ queryKey: ["/api/admin/top-candidates"] });
    },
    onError: () => {
      toast({
        title: "Erreur",
        description: "Erreur lors de l'affectation des candidats.",
        variant: "destructive",
      });
    }
  });

  const handleCandidateSelect = (candidateId: number, checked: boolean) => {
    if (checked) {
      setSelectedCandidates(prev => [...prev, candidateId]);
    } else {
      setSelectedCandidates(prev => prev.filter(id => id !== candidateId));
    }
  };

  const handleAssignCandidates = () => {
    if (selectedCandidates.length === 0 || !selectedRecruiter) {
      toast({
        title: "Sélection incomplète",
        description: "Veuillez sélectionner des candidats et un recruteur.",
        variant: "destructive",
      });
      return;
    }

    assignMutation.mutate({
      candidateIds: selectedCandidates,
      recruiterId: selectedRecruiter
    });
  };

  const getScoreColor = (score: number) => {
    if (score >= 80) return "text-green-600 font-semibold";
    if (score >= 60) return "text-blue-600 font-semibold";
    if (score >= 40) return "text-yellow-600 font-semibold";
    return "text-red-600 font-semibold";
  };

  const getScoreBadge = (score: number) => {
    if (score >= 80) return "bg-green-100 text-green-800";
    if (score >= 60) return "bg-blue-100 text-blue-800";
    if (score >= 40) return "bg-yellow-100 text-yellow-800";
    return "bg-red-100 text-red-800";
  };

  const handleLogout = () => {
    window.location.href = "/api/logout";
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="bg-card shadow-sm border-b border-border">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-2">
              <Briefcase className="h-8 w-8 text-primary" />
              <span className="text-xl font-bold text-foreground">Admin RH</span>
            </div>

            <div className="flex items-center space-x-4">
              <span className="text-sm text-muted-foreground">
                {user?.firstName} {user?.lastName}
              </span>
              <Badge variant="secondary">
                {user?.role === "admin" ? "Super Admin" : 
                 user?.role === "hr" ? "RH" : "Recruteur"}
              </Badge>
              <Button variant="ghost" size="sm" onClick={handleLogout}>
                <LogOut className="h-4 w-4 mr-2" />
                Déconnexion
              </Button>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Navigation */}
        <div className="flex space-x-6 mb-8 border-b border-border">
          <Link href="/admin">
            <Button variant="ghost" className="text-muted-foreground">
              <Briefcase className="h-4 w-4 mr-2" />
              Tableau de bord
            </Button>
          </Link>
          <Button variant="ghost" className="border-b-2 border-primary text-primary">
            <Target className="h-4 w-4 mr-2" />
            Affectation Candidats
          </Button>
          <Link href="/admin/scoring">
            <Button variant="ghost" className="text-muted-foreground">
              <Award className="h-4 w-4 mr-2" />
              Notation
            </Button>
          </Link>
        </div>

        <h1 className="text-2xl font-bold mb-6">Affectation des Candidats - Top 10 Automatique</h1>

        {/* Job Selection */}
        <Card className="mb-6">
          <CardHeader>
            <CardTitle>1. Sélectionner l'offre d'emploi</CardTitle>
          </CardHeader>
          <CardContent>
            <Select value={selectedJob} onValueChange={setSelectedJob}>
              <SelectTrigger className="w-full">
                <SelectValue placeholder="Choisissez une offre d'emploi" />
              </SelectTrigger>
              <SelectContent>
                {jobs.map((job: any) => (
                  <SelectItem key={job.id} value={job.id.toString()}>
                    {job.title} - {job.company}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </CardContent>
        </Card>

        {/* Top 10 Candidates */}
        {selectedJob && (
          <Card className="mb-6">
            <CardHeader>
              <CardTitle className="flex items-center">
                <Star className="h-5 w-5 mr-2 text-yellow-500" />
                2. Top 10 des Meilleurs Candidats (Tri Automatique)
              </CardTitle>
            </CardHeader>
            <CardContent>
              {isLoading ? (
                <div className="text-center py-8">Calcul des scores en cours...</div>
              ) : topCandidates.length === 0 ? (
                <div className="text-center py-8 text-muted-foreground">
                  Aucune candidature pour cette offre
                </div>
              ) : (
                <div className="space-y-4">
                  {topCandidates.map((candidate: CandidateScore, index: number) => (
                    <div key={candidate.applicationId} className="border rounded-lg p-4">
                      <div className="flex items-start space-x-4">
                        <Checkbox
                          checked={selectedCandidates.includes(candidate.applicationId)}
                          onCheckedChange={(checked) => 
                            handleCandidateSelect(candidate.applicationId, checked as boolean)
                          }
                        />

                        <div className="flex-1">
                          <div className="flex items-center space-x-3 mb-2">
                            <Badge variant="outline" className="text-xs">
                              #{index + 1}
                            </Badge>
                            <h4 className="text-lg font-semibold">
                              {candidate.candidate.firstName} {candidate.candidate.lastName}
                            </h4>
                            <Badge className={getScoreBadge(candidate.totalScore)}>
                              {candidate.totalScore}/100
                            </Badge>
                          </div>

                          <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-3">
                            <div className="text-sm">
                              <strong>Email:</strong> {candidate.candidate.email}
                            </div>
                            <div className="text-sm">
                              <strong>Téléphone:</strong> {candidate.candidate.phone || 'Non renseigné'}
                            </div>
                          </div>

                          {/* Score Details */}
                          <div className="grid grid-cols-2 md:grid-cols-5 gap-2 text-xs">
                            <div className="text-center p-2 bg-gray-50 rounded">
                              <div className="font-semibold">Expérience</div>
                              <div className={getScoreColor(candidate.factors.experienceMatch)}>
                                {candidate.factors.experienceMatch}/25
                              </div>
                            </div>
                            <div className="text-center p-2 bg-gray-50 rounded">
                              <div className="font-semibold">Compétences</div>
                              <div className={getScoreColor(candidate.factors.skillsMatch)}>
                                {candidate.factors.skillsMatch}/30
                              </div>
                            </div>
                            <div className="text-center p-2 bg-gray-50 rounded">
                              <div className="font-semibold">Disponibilité</div>
                              <div className={getScoreColor(candidate.factors.availabilityScore)}>
                                {candidate.factors.availabilityScore}/15
                              </div>
                            </div>
                            <div className="text-center p-2 bg-gray-50 rounded">
                              <div className="font-semibold">Salaire</div>
                              <div className={getScoreColor(candidate.factors.salaryFit)}>
                                {candidate.factors.salaryFit}/15
                              </div>
                            </div>
                            <div className="text-center p-2 bg-gray-50 rounded">
                              <div className="font-semibold">Qualité</div>
                              <div className={getScoreColor(candidate.factors.applicationQuality)}>
                                {candidate.factors.applicationQuality}/15
                              </div>
                            </div>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </CardContent>
          </Card>
        )}

        {/* Assignment Section */}
        {selectedCandidates.length > 0 && (
          <Card>
            <CardHeader>
              <CardTitle>3. Affecter aux Recruteurs pour Notation</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                <div>
                  <p className="text-sm text-muted-foreground mb-2">
                    {selectedCandidates.length} candidat(s) sélectionné(s)
                  </p>

                  <Select value={selectedRecruiter} onValueChange={setSelectedRecruiter}>
                    <SelectTrigger className="w-full">
                      <SelectValue placeholder="Choisissez un recruteur" />
                    </SelectTrigger>
                    <SelectContent>
                      {recruiters.map((recruiter: any) => (
                        <SelectItem key={recruiter.id} value={recruiter.id}>
                          {recruiter.firstName} {recruiter.lastName} - {recruiter.role}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <Button 
                  onClick={handleAssignCandidates}
                  disabled={assignMutation.isPending}
                  className="w-full"
                >
                  <UserCheck className="h-4 w-4 mr-2" />
                  {assignMutation.isPending ? "Attribution en cours..." : "Affecter les Candidats"}
                </Button>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

client/src/pages/CandidateDashboard.tsx.content:
import { useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import { useAuth } from "@/hooks/useAuth";
import { useToast } from "@/hooks/use-toast";
import { Link } from "wouter";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { 
  Briefcase, 
  Bell, 
  User, 
  LogOut, 
  LayoutDashboard,
  FileText,
  Search,
  Folder,
  Send,
  Clock,
  Calendar,
  UserCheck,
  CheckCircle,
  Circle
} from "lucide-react";
import { t } from "@/lib/i18n";
import { LanguageSelector } from "@/components/LanguageSelector";
import { formatDistanceToNow } from "date-fns";
import { fr, enUS, pt } from "date-fns/locale";
import { getLanguage } from "@/lib/i18n";
import { ApplicationTimeline } from "@/components/ApplicationTimeline";

const locales = {
  fr: fr,
  en: enUS,
  pt: pt,
};

export default function CandidateDashboard() {
  const { user, isLoading, isAuthenticated } = useAuth();
  const { toast } = useToast();
  const currentLang = getLanguage();
  const locale = locales[currentLang];

  const { data: applications = [], isLoading: applicationsLoading } = useQuery({
    queryKey: ["/api/applications"],
    enabled: isAuthenticated,
  });

  useEffect(() => {
    if (!isLoading && !isAuthenticated) {
      toast({
        title: "Non autorisé",
        description: "Vous devez être connecté. Redirection vers la connexion...",
        variant: "destructive",
      });
      setTimeout(() => {
        window.location.href = "/api/login";
      }, 500);
      return;
    }
  }, [isAuthenticated, isLoading, toast]);

  if (isLoading) {
    return (
      <div className="min-h-screen bg-background flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary"></div>
          <p className="mt-4 text-muted-foreground">Chargement...</p>
        </div>
      </div>
    );
  }

  if (!isAuthenticated || !user) {
    return null;
  }

  const stats = {
    applicationsCount: applications.length,
    pendingCount: applications.filter(app => app.status === 'pending').length,
    interviewsCount: applications.filter(app => app.status === 'interview').length,
    profileCompletion: 85, // This would be calculated based on completed profile fields
  };

  const recentApplications = applications.slice(0, 3);

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending':
        return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-100';
      case 'reviewed':
        return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-100';
      case 'interview':
        return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-100';
      case 'accepted':
        return 'bg-emerald-100 text-emerald-800 dark:bg-emerald-900 dark:text-emerald-100';
      case 'rejected':
        return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-100';
      default:
        return 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-100';
    }
  };

  const getStatusText = (status: string) => {
    return t(`status_${status}` as any) || status;
  };

  return (
    <div className="min-h-screen bg-background">
      {/* Header */}
      <header className="bg-card shadow-sm border-b border-border">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center space-x-8">
              <div className="flex items-center space-x-2">
                <Briefcase className="h-8 w-8 text-primary" />
                <span className="text-xl font-bold text-foreground">JobPortal</span>
                <span className="text-sm text-muted-foreground">Espace candidat</span>
              </div>
            </div>

            <div className="flex items-center space-x-4">
              <LanguageSelector />

              {/* Notifications */}
              <Button variant="ghost" size="icon" className="relative" data-testid="button-notifications">
                <Bell className="h-5 w-5" />
                <span className="absolute -top-1 -right-1 h-4 w-4 bg-destructive text-destructive-foreground text-xs rounded-full flex items-center justify-center">
                  3
                </span>
              </Button>

              {/* User Menu */}
              <div className="flex items-center space-x-2">
                <div className="h-8 w-8 bg-primary text-primary-foreground rounded-full flex items-center justify-center text-sm font-medium">
                  {user.firstName?.[0] || user.email?.[0] || 'U'}
                </div>
                <span className="text-sm font-medium" data-testid="text-user-name">
                  {user.firstName && user.lastName ? `${user.firstName} ${user.lastName}` : user.email}
                </span>
                <Button 
                  variant="ghost" 
                  size="icon"
                  onClick={() => import("@/lib/logout").then(m => m.handleLogout())}
                  data-testid="button-logout"
                  title="Se déconnecter"
                >
                  <LogOut className="h-4 w-4" />
                </Button>
              </div>
            </div>
          </div>
        </div>
      </header>

      <div className="flex">
        {/* Sidebar Navigation */}
        <nav className="w-64 bg-card shadow-sm border-r border-border min-h-screen">
          <div className="p-6">
            <ul className="space-y-2">
              <li>
                <Link href="/">
                  <a className="flex items-center space-x-3 p-3 text-primary bg-primary/10 rounded-md">
                    <LayoutDashboard className="h-5 w-5" />
                    <span className="font-medium">{t('dashboard')}</span>
                  </a>
                </Link>
              </li>
              <li>
                <Link href="/applications">
                  <a className="flex items-center space-x-3 p-3 text-muted-foreground hover:text-foreground hover:bg-accent rounded-md transition-colors" data-testid="link-applications">
                    <FileText className="h-5 w-5" />
                    <span>Mes candidatures</span>
                  </a>
                </Link>
              </li>
              <li>
                <Link href="/profile">
                  <a className="flex items-center space-x-3 p-3 text-muted-foreground hover:text-foreground hover:bg-accent rounded-md transition-colors" data-testid="link-profile">
                    <User className="h-5 w-5" />
                    <span>Mon profil</span>
                  </a>
                </Link>
              </li>
              <li>
                <Link href="/onboarding">
                  <a className="flex items-center space-x-3 p-3 text-muted-foreground hover:text-foreground hover:bg-accent rounded-md transition-colors" data-testid="link-onboarding">
                    <UserCheck className="h-5 w-5" />
                    <span>Mon onboarding</span>
                  </a>
                </Link>
              </li>
              <li>
                <Link href="/jobs">
                  <a className="flex items-center space-x-3 p-3 text-muted-foreground hover:text-foreground hover:bg-accent rounded-md transition-colors" data-testid="link-search">
                    <Search className="h-5 w-5" />
                    <span>Rechercher</span>
                  </a>
                </Link>
              </li>
              <li>
                <a href="#documents" className="flex items-center space-x-3 p-3 text-muted-foreground hover:text-foreground hover:bg-accent rounded-md transition-colors" data-testid="link-documents">
                  <Folder className="h-5 w-5" />
                  <span>Mes documents</span>
                </a>
              </li>
            </ul>
          </div>
        </nav>

        {/* Main Content */}
        <main className="flex-1 p-8 bg-background">
          <div className="mb-8">
            <h1 className="text-3xl font-bold text-foreground mb-2" data-testid="text-dashboard-title">
              {t('dashboard')}
            </h1>
            <p className="text-muted-foreground" data-testid="text-welcome-message">
              {t('welcome')}
            </p>
          </div>

          {/* Stats Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
            <Card className="shadow-sm">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm text-muted-foreground">{t('applications_sent')}</p>
                    <p className="text-2xl font-bold text-foreground" data-testid="stat-applications-count">
                      {stats.applicationsCount}
                    </p>
                  </div>
                  <Send className="h-8 w-8 text-primary" />
                </div>
              </CardContent>
            </Card>

            <Card className="shadow-sm">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm text-muted-foreground">{t('pending')}</p>
                    <p className="text-2xl font-bold text-foreground" data-testid="stat-pending-count">
                      {stats.pendingCount}
                    </p>
                  </div>
                  <Clock className="h-8 w-8 text-yellow-500" />
                </div>
              </CardContent>
            </Card>

            <Card className="shadow-sm">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm text-muted-foreground">{t('interviews')}</p>
                    <p className="text-2xl font-bold text-foreground" data-testid="stat-interviews-count">
                      {stats.interviewsCount}
                    </p>
                  </div>
                  <Calendar className="h-8 w-8 text-green-500" />
                </div>
              </CardContent>
            </Card>

            <Card className="shadow-sm">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm text-muted-foreground">{t('profile_completed')}</p>
                    <p className="text-2xl font-bold text-foreground" data-testid="stat-profile-completion">
                      {stats.profileCompletion}%
                    </p>
                  </div>
                  <UserCheck className="h-8 w-8 text-blue-500" />
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Timeline de candidature principale */}
          {recentApplications.length > 0 && (
            <div className="mb-8">
              <ApplicationTimeline application={recentApplications[0]} />
            </div>
          )}

          {/* Recent Applications and Profile Completion */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
            <Card className="shadow-sm">
              <CardContent className="p-6">
                <h3 className="text-lg font-semibold text-foreground mb-4">
                  {t('recent_applications')}
                </h3>
                {applicationsLoading ? (
                  <div className="space-y-4">
                    {[1, 2, 3].map((i) => (
                      <div key={i} className="animate-pulse p-4 bg-background rounded-lg">
                        <div className="h-4 bg-muted rounded w-3/4 mb-2"></div>
                        <div className="h-3 bg-muted rounded w-1/2"></div>
                      </div>
                    ))}
                  </div>
                ) : recentApplications.length === 0 ? (
                  <p className="text-muted-foreground text-center py-8" data-testid="text-no-applications">
                    Aucune candidature envoyée pour le moment.
                  </p>
                ) : (
                  <div className="space-y-4">
                    {recentApplications.map((application) => (
                      <div 
                        key={application.id}
                        className="flex items-center justify-between p-4 bg-background rounded-lg"
                        data-testid={`application-item-${application.id}`}
                      >
                        <div className="flex-1">
                          <h4 className="font-medium text-foreground" data-testid={`text-job-title-${application.id}`}>
                            {application.job?.title || 'Poste supprimé'}
                          </h4>
                          <p className="text-sm text-muted-foreground" data-testid={`text-company-${application.id}`}>
                            {application.job?.company || 'Entreprise inconnue'}
                          </p>
                          <p className="text-xs text-muted-foreground" data-testid={`text-date-${application.id}`}>
                            Candidature envoyée {formatDistanceToNow(application.createdAt, { addSuffix: true, locale })}
                          </p>
                        </div>
                        <div className="text-right">
                          <Badge 
                            className={getStatusColor(application.status)}
                            data-testid={`badge-status-${application.id}`}
                          >
                            {getStatusText(application.status)}
                          </Badge>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
                <div className="mt-4">
                  <Link href="/applications">
                    <a className="text-primary hover:text-primary/80 text-sm font-medium" data-testid="link-view-all-applications">
                      Voir toutes mes candidatures →
                    </a>
                  </Link>
                </div>
              </CardContent>
            </Card>

            {/* Profile Completion */}
            <Card className="shadow-sm">
              <CardContent className="p-6">
                <h3 className="text-lg font-semibold text-foreground mb-4">
                  Complétez votre profil
                </h3>
                <div className="space-y-4">
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-foreground">Informations personnelles</span>
                    <CheckCircle className="h-5 w-5 text-green-500" />
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-foreground">Email vérifié</span>
                    <CheckCircle className="h-5 w-5 text-green-500" />
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-muted-foreground">Photo de profil</span>
                    <Circle className="h-5 w-5 text-muted-foreground" />
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-muted-foreground">Expériences professionnelles</span>
                    <Circle className="h-5 w-5 text-muted-foreground" />
                  </div>
                  <div className="flex items-center justify-between">
                    <span className="text-sm text-muted-foreground">Compétences</span>
                    <Circle className="h-5 w-5 text-muted-foreground" />
                  </div>
                </div>
                <div className="mt-6">
                  <Link href="/profile">
                    <Button className="w-full" data-testid="button-complete-profile">
                      Compléter mon profil
                    </Button>
                  </Link>
                </div>
              </CardContent>
            </Card>
          </div>
        </main>
      </div>
    </div>
  );
}

function getStatusColor(status: string) {
  switch (status) {
    case 'pending':
      return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-100';
    case 'reviewed':
      return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-100';
    case 'interview':
      return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-100';
    case 'accepted':
      return 'bg-emerald-100 text-emerald-800 dark:bg-emerald-900 dark:text-emerald-100';
    case 'rejected':
      return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-100';
    default:
      return 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-100';
  }
}

function getStatusText(status: string) {
  const translations = {
    pending: "En attente",
    reviewed: "Examinée", 
    interview: "Entretien planifié",
    accepted: "Acceptée",
    rejected: "Refusée",
  };
  return translations[status as keyof typeof translations] || status;
}

